// Define the lead_number
let lead_number = $('Get Record From Campaign Table - 1').first().json['Number Of Leads'];

// Function to generate the array
function generateIds(lead_number) {
  const count = Math.ceil(lead_number / 20);
  const result = [];

  for (let i = 1; i <= count; i++) {
    result.push({ id: i }); // Only { id: i } -- no extra wrapping
  }

  return result;
}

// Call the function
return generateIds(lead_number);

